import { types } from "./type";

let initialState = {
    users : [],
    loggedInUser : null
};
export const authReducer = (state = initialState, action) => {
    switch (action.type) {
        case types.REGISTRATION_REQUEST:
            return {
                ...state,
                users : [...state.users, action.payload]
            };

            return {
                ...state,
                loading: false,
            };
        default: {
            return state;
        }
    }
};
