import React from "react";
import './App.css';

import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import Container from '@mui/material/Container';
import LeftSideBar from "./component/leftSideBar";
import RegistrationForm from "./component/registrationForm";
import ThemeSwitch from "./component/themeSwitch";

// const theme = createTheme();
const ColorModeContext = React.createContext({ toggleColorMode: () => { } });

function App() {

  const [mode, setMode] = React.useState('light');
  const colorMode = React.useMemo(
    () => ({
      toggleColorMode: () => {
        setMode((prevMode) => (prevMode === 'light' ? 'dark' : 'light'));
      },
    }),
    [],
  );

  const theme = React.useMemo(
    () =>
      createTheme({
        palette: {
          mode,
        },
      }),
    [mode],
  );

  return (
    <ColorModeContext.Provider value={colorMode}>
      <ThemeProvider theme={theme}>
        <container spacing={2} component="main">
          <CssBaseline />
          <LeftSideBar changemode={theme.palette.mode} />
          <Grid item xs={12} sm={6} md={6} component={Paper} elevation={6} square>
            <ThemeSwitch handleSwithch={colorMode.toggleColorMode} changemode={theme.palette.mode} />
            <RegistrationForm changemode={theme.palette.mode} />
          </Grid>
        </container>
      </ThemeProvider>
    </ColorModeContext.Provider>
  );
}

export default App;
