import * as React from 'react';
import IconButton from '@mui/material/IconButton';
import Box from '@mui/material/Box';
import { useTheme, ThemeProvider, createTheme } from '@mui/material/styles';
import Brightness7Icon from '@mui/icons-material/Brightness7';
import Brightness2OutlinedIcon from '@mui/icons-material/Brightness2Outlined';
import Grid from '@mui/material/Grid';
import CssBaseline from '@mui/material/CssBaseline';
import Paper from '@mui/material/Paper';
import RakBankLogo from "../img/raklogo.png";


function ThemeSwitch(props) {

  return (
    <Grid container spacing={2} sx={{ mt: 8, mb: 2 }}>
      <CssBaseline />
      <Grid item xs={12} sm={6} md={6} component={Paper} elevation={6} square>
      <img
        src={`${RakBankLogo}`}
        alt={'RAKBANK'}
        loading="lazy"
      />
      </Grid>
      <Grid item xs={12} sm={6} md={6} className="textright" component={Paper} elevation={6} square>
        <IconButton sx={{ ml: 1 }} onClick={props?.handleSwithch} color="inherit">
          {props?.changemode === 'dark' ? <Brightness2OutlinedIcon /> : <Brightness7Icon />}
        </IconButton>
      </Grid>
    </Grid>
  );
}

export default ThemeSwitch;
