import { types } from "./type";

let initialState = {
    lightToDark: true
};
export const themeSwitcher = (state = initialState, action) => {
    switch (action.type) {
        case types.TOOGLE_THEME:
            return {
                ...state,
                lightToDark: action.payload,
            };
        default: {
            return state;
        }
    }
};
