import React, { useEffect } from "react";
import './App.css';

import CssBaseline from '@mui/material/CssBaseline';
import Grid from '@mui/material/Grid';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Paper from '@mui/material/Paper';
import LeftSideBar from "./component/leftSideBar";
import RegistrationForm from "./component/registrationForm";
import ThemeSwitch from "./component/themeSwitch";
import { connect } from "react-redux";

// const theme = createTheme();
const ColorModeContext = React.createContext({ toggleColorMode: () => { } });

function App(props) {

  const [mode, setMode] = React.useState('light');

  useEffect(() => {
    console.log("setMode  :: ", props?.theme?.theme_Toogle)
  });
  const colorMode = React.useMemo(
    () => ({
      toggleColorMode: () => {
        setMode(props?.theme?.theme_Toogle ? 'dark' : 'light');
      },
    }),
    [],
  );

  const theme = React.useMemo(
    () =>
      createTheme({
        palette: {
          mode,
        },
      }),
    [mode],
  );

  return (
    <ColorModeContext.Provider value={colorMode}>
      {console.log("theme :: ", props?.theme?.theme_Toogle)}
      <ThemeProvider theme={theme}>
        <Grid container spacing={2} sx={{ mt: 8, mb: 8, pl: 15, pr: 15, }} component="main">
          <CssBaseline />
          <LeftSideBar changemode={theme.palette.mode} />
          <Grid item xs={12} sm={6} md={6} component={Paper} elevation={6} square>
            <ThemeSwitch handleSwithch={colorMode.toggleColorMode} changemode={theme.palette.mode} />
            <RegistrationForm changemode={theme.palette.mode} />
          </Grid>
        </Grid>
      </ThemeProvider>
    </ColorModeContext.Provider>
  );
}

// export default App;
const mapStateToProps = (state) => {
  return {
    theme: state.themeLightToDark,
  };
};
export default connect(mapStateToProps)(App);
