import {
    createStore,
    combineReducers,
    applyMiddleware,
    compose,
} from "redux";
import thunk from "redux-thunk";
import reducers from "./users/index";
import { authReducer } from "./users/reducers";
import { themeSwitcher } from "./themeSwitching/reducers";
import storage from "redux-persist/lib/storage";
import {
    persistStore,
    persistReducer,
} from "redux-persist";
import createSagaMiddleware from "redux-saga";
// import { all, fork } from "redux-saga/effects";

const persistConfig = {
    key: "root",
    storage,
};

const rootReducer = combineReducers({
    userReducer: authReducer,
    themeLightToDark: themeSwitcher,
});

// const persistedReducer = persistReducer(
//     persistConfig,
//     rootReducer
// );

const sagaMiddleware = createSagaMiddleware();

let enhancer;
if (window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__) {
    enhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__(
        applyMiddleware(sagaMiddleware, thunk)
    );
} else {
    enhancer = compose(applyMiddleware(sagaMiddleware, thunk));
}

export const store = createStore(
    rootReducer,
    enhancer
);

